#include <stdio.h>

#define MAX(a, b) ((a > b) ? a : b)
#define MIN(a, b) ((a < b) ? a : b)

int main(){
  int a, b;
  scanf("%d %d", &a, &b);
  int first = MIN(a, b);
  int left = (MAX(a, b) - first) / 2;
  printf("%d %d\n", first, left);
  return 0;
}

