#include<stdio.h>
#include<stdlib.h>

int main()
{
    int d1,d2,d3,lowest;

    scanf("%d%d%d",&d1,&d2,&d3);

    if((d1+d2+d3) >= 2*(d1+d2))
    {
        lowest = 2*(d1+d2);
    }
    else
    {
        lowest = (d1+d2+d3);
    }
    if(lowest > 2*(d1+d3))
    {
        lowest = 2*(d1+d3);
    }
    if(lowest > 2*(d2+d3))
    {
        lowest = 2*(d2+d3);
    }

    printf("%d",lowest);

    return 0;
}

