#include <stdio.h>
int main ()
{
    int a, b, c, x;
    scanf("%d %d %d",&a, &b, &c);
    if((2*a+2*b)>=a+b+c)
    {
       x=a+b+c;

    }
    else {
        x=2*a+2*b;

    }
    if(x>=2*a+2*c)
    {
       x=2*a+2*c;

    }
    if(x>=2*b+2*c)
    {
        x=2*b+2*c;
    }
    printf("%d\n",x);
    return 0;
}
